import React, { useState } from "react";

function Form() {
  const [formDataList, setFormDataList] = useState([{ id: 1 }]);

  const createPersonData = () => ({
    citizenship: "",
    countryOfBirth: "",
    countyOfResidence: "",
    cityOfResidence: "",
    dateOfBirth: "",
    documentNumber: "",
    firstName: "",
    lastName: "",
  });

  const MAX_CHAR_LENGTH = 100;

  const handleInputChange = (e, index) => {
    const value = e.target.value.slice(0, MAX_CHAR_LENGTH);
    setFormDataList((prevFormDataList) => {
      const updatedFormDataList = [...prevFormDataList];
      updatedFormDataList[index] = {
        ...updatedFormDataList[index],
        citizenship: value,
        countryOfBirth: value,
        countyOfResidence: value,
        cityOfResidence: "",
      };
      return updatedFormDataList;
    });
  };

  const handleNameInput = (e, index) => {
    const value = e.target.value.slice(0, MAX_CHAR_LENGTH);
    setFormDataList((prevFormDataList) => {
      const updatedFormDataList = [...prevFormDataList];
      updatedFormDataList[index] = {
        ...updatedFormDataList[index],
        firstName: value,
      };
      return updatedFormDataList;
    });
  };

  const handleLastnameInput = (e, index) => {
    const value = e.target.value.slice(0, MAX_CHAR_LENGTH);
    setFormDataList((prevFormDataList) => {
      const updatedFormDataList = [...prevFormDataList];
      updatedFormDataList[index] = {
        ...updatedFormDataList[index],
        lastName: value,
      };
      return updatedFormDataList;
    });
  };

  const handleDocumentNumberChange = (e, index) => {
    const value = e.target.value.slice(0, 16);
    setFormDataList((prevFormDataList) => {
      const updatedFormDataList = [...prevFormDataList];
      updatedFormDataList[index] = {
        ...updatedFormDataList[index],
        documentNumber: value,
      };
      return updatedFormDataList;
    });
  };

  const handleAddPerson = (e) => {
    e.preventDefault();
    setFormDataList((prevFormDataList) => [
      ...prevFormDataList,
      createPersonData(),
    ]);
  };

  const handleRemovePerson = (e) => {
    e.preventDefault();
    if (formDataList.length > 1) {
      setFormDataList((prevFormDataList) => prevFormDataList.slice(0, -1));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validacija i pohrana podataka
    console.log(formDataList);
  };

  return (
    <main>
      <div className="second-container">
        {formDataList.map((formData, index) => (
          <div key={index} className="form-container">
            <form id={`form-${index}`}>
              <div className="fields-form">
                <div className="person-1">
                  <div className="field-form">
                    <label htmlFor="firstName" style={{ fontSize: "23px" }}>
                      <u>{`PERSON ${index + 1}`}</u>
                    </label>
                  </div>
                  <div className="field-form">
                    <label htmlFor="firstName">Name / Ime </label>
                    <input
                      type="text"
                      name="firstName"
                      id="firstName"
                      placeholder=""
                      value={formData.firstName}
                      onChange={(e) => handleNameInput(e, index)}
                    />
                  </div>
                  <div className="field-form">
                    <label htmlFor="lastName">Lastname / Prezime </label>
                    <input
                      type="text"
                      name="lastName"
                      id="lastName"
                      placeholder=""
                      value={formData.lastName}
                      onChange={(e) => handleLastnameInput(e, index)}
                    />
                  </div>
                  <div className="field-form">
                    {/* Ostali inputi za ostale atribute */}
                  </div>
                  <p className="for-button" style={{ paddingBottom: "15px" }}>
                    <button
                      id={`toggle-person-${index}`}
                      className="form-btn"
                      onClick={(e) => {
                        if (formDataList.length < 10 && index === formDataList.length - 1) {
                          handleAddPerson(e);
                        } else {
                          handleRemovePerson(e);
                        }
                      }}
                    >
                      {index === formDataList.length - 1
                        ? "ADD NEXT PERSON"
                        : "REMOVE PERSON"}
                    </button>
                  </p>
                </div>
              </div>
            </form>
          </div>
        ))}
      </div>
      <div className="fields-form">
        <p className="for-button">
          <button className="form-btn" onClick={handleSubmit}>
            Submit / Potvrdi
          </button>
        </p>
      </div>
    </main>
  );
}

export default Form;
