import React, {useEffect} from 'react';
import { useSearchParams, useLocation, useNavigate} from 'react-router-dom';
import { useLanguage } from './Language';
function TitleSection() {
    const location = useLocation();
    const identifier = new URLSearchParams(location.search).get('q');
    const [searchParams] = useSearchParams();
    const q = searchParams.get('q');
    const { queryParam,  } = useLanguage();
    const navigate = useNavigate(); // Dohvatite navigate funkciju
  
    useEffect(() => {
      const queryParams = new URLSearchParams(location.search);
      queryParams.set('q', queryParam);
      
      // Ažurirajte URL bez osvježavanja stranice
      navigate(`?q=${queryParam}`);
    }, [queryParam, location.search, navigate]);
  
    return (
    < div className = "section-head" >
        <div className="section-container">
            <div className="section-titles">
                    <h1 className="section-title">{ q || identifier || 'Forest Hill'}</h1>
                <h1 className="section-title">CHECK-IN</h1>
            </div>
        </div>
      </div >
    )
}

export default TitleSection;
