import React from 'react';
import { useLocation } from 'react-router-dom';

function TitleSection() {
    const location = useLocation();
    const navigate = useNavigate();
    const queryParams = new URLSearchParams(location.search);
    const q = queryParams.get('q');
    const { language, changeLanguage } = useLanguage();
    const handleLanguageChange = (newLanguage) => {
        // Dohvatite trenutni URL i dodajte query parametar 'q' ako je prisutan
        const currentURL = new URL(window.location.href);
        if (q) {
            currentURL.searchParams.set('q', q);
        }
        
        // Ažurirajte jezik u query stringu
        currentURL.searchParams.set('language', newLanguage);

        // Koristite navigate za promjenu URL-a bez osvježavanja stranice
        navigate(`${currentURL.pathname}${currentURL.search}`);
    };
    return (
    < div className = "section-head" >
        <div className="section-container">
            <div className="section-titles">
                <h1 className="section-title">{q || 'Forest Hill'}</h1>
                <h1 className="section-title">CHECK-IN</h1>
            </div>
        </div>
      </div >
    )
}

export default TitleSection;
