import React, { createContext, useContext, useState } from 'react';

// Kreiranje konteksta za jezik
const LanguageContext = createContext();

// Pružanje komponente za upravljanje jezikom
export function LanguageProvider({ children }) {
  const [language, setLanguage] = useState('en'); // Inicijalni jezik

  // Funkcija za promjenu jezika
  const changeLanguage = (newLanguage) => {
    setLanguage(newLanguage);
  };

  // Vrijednosti koje će biti dostupne komponentama koje koriste ovaj kontekst
  const contextValue = {
    language,
    changeLanguage,
  };

  return (
    // Pruzimanje konteksta na razini aplikacije
    <LanguageContext.Provider value={contextValue}>
      {children}
    </LanguageContext.Provider>
  );
}

// Prilagođeni kuka za korištenje jezičnog konteksta
export function useLanguage() {
  return useContext(LanguageContext);
}
